query BlogPostBySlug(
  $slug: String!
) {
  site {
    siteMetadata {
      title
      author
    }
  }
  markdownRemark(fields: {slug: {eq: $slug}}) {
    id
    htmlAst
    frontmatter {
      title
      subject
      date(formatString: "MMMM DD, YYYY")
    }
  }
}

query SubjectByName(
  $subject: String!
) {
  site {
    siteMetadata {
      title
      author
    }
  }
  allMarkdownRemark(sort: {fields: [frontmatter___date], order: DESC}, filter: {frontmatter: {subject: {eq: $subject}}}) {
    edges {
      node {
        excerpt
        fields {
          slug
        }
        frontmatter {
          date(formatString: "DD MMMM, YYYY")
          title
          subject
        }
      }
    }
  }
}

